var el = document.getElementById("maybe");
var icons = document.getElementsByClassName("mobile");
var pad1 = document.getElementById("services");
var pad2 = document.getElementById("portfolio");
var pad3 = document.getElementById("about");
var quote1 = document.getElementById("knowledge");
var quote2 = document.getElementById("kills");
var quote3 = document.getElementById("story");
var more = document.getElementById("smaller");
var battlespace = document.getElementById("battlespace");
var modals = document.getElementsByClassName("modals");
var subheaders = document.getElementsByClassName ("subheader ");
var imgg = document.getElementById("swapPhoto");

if( /Android|iPhone|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)) {

    /* the 4th book should show up in the Books section to make it a 2x2 grid */
    el.classList.remove("showw", "collapse");

    /* make text inside modals more readible on mobiles */
    for (var i = 0; i < modals.length; i++) {
        modals[i].style.fontSize = "25px";

    }
    for (var i = 0; i < subheaders.length; i++) {
        subheaders[i].style.marginBottom = "0px";

    }

    /*icons in services section should be a little smaller */
    for (var i = 0; i < icons.length; i++) {
        icons[i].classList.remove("fa-4x");
        icons[i].classList.add("fa-3x");
    }

    /* page sections should have a little less padding */
    pad1.style.paddingTop = "85px";
    pad1.style.paddingBottom = "75px";
    pad2.style.paddingTop = "75px";
    pad2.style.paddingBottom = "100px";
    pad3.style.paddingTop = "75px";
    pad3.style.paddingBottom = "25px";

    /* quotes should have a little less margin under them */
    quote1.style.marginBottom = "50px";
    quote2.style.marginBottom = "50px";
    quote3.style.marginBottom = "50px";

    /* make "Learn More" button smaller */
    more.classList.remove("btn-xl");
    more.classList.add("btn-lg");

    /* make Title "The Battlespace" slightly smaller to avoid overflow issues */
    battlespace.style.fontSize = "45px";

    /* all of the books become the same size */
    imgg.removeAttribute("src");
    imgg.setAttribute("src", "Home/images/combatShortened.png");
}

function adjustBooksWithScreen() {
    if (window.innerWidth < 768 && el.classList.contains('showw')) {
        el.classList.remove("showw", "collapse");
    }
    else if (window.innerWidth > 768 && !(el.classList.contains('showw'))) {
        el.classList.add("showw", "collapse");
    }
}
window.onresize = adjustBooksWithScreen;

!function (e) {
    "use strict";
    e('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function () {
        if (location.pathname.replace(/^\//, "") == this.pathname.replace(/^\//, "") && location.hostname == this.hostname) {
            var a = e(this.hash);
            if ((a = a.length ? a : e("[name=" + this.hash.slice(1) + "]")).length) return e("html, body").animate({scrollTop: a.offset().top - 54}, 1e3, "easeInOutExpo"), !1
        }
    }), e(".js-scroll-trigger").click(function () {
        e(".navbar-collapse").collapse("hide")
    }), e("body").scrollspy({target: "#mainNav", offset: 56});

    function a() {
        100 < e("#mainNav").offset().top ? e("#mainNav").addClass("navbar-shrink") : e("#mainNav").removeClass("navbar-shrink")
    }

    a(), e(window).scroll(a)

    /* this stuff messes with the nav bar */
    var btn = document.getElementById('button');

    btn.addEventListener("click", function(event) {
        if (btn.innerHTML == "More...") {
            btn.innerHTML = "Less";
        }
        else {
            btn.innerHTML = "More...";
        }
    });

    /* For mobile users */





}(jQuery);